(window.webpackJsonp=window.webpackJsonp||[]).push([[27],{495:function(v,_,s){"use strict";s.r(_);var t=s(29),p=Object(t.a)({},(function(){var v=this,_=v.$createElement,s=v._self._c||_;return s("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[s("h1",{attrs:{id:"但愿在生活里拼尽全力的人-也都能被生活放过吧"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#但愿在生活里拼尽全力的人-也都能被生活放过吧"}},[v._v("#")]),v._v(" 但愿在生活里拼尽全力的人，也都能被生活放过吧")]),v._v(" "),s("hr"),v._v(" "),s("p",[v._v("《许三观卖血记》很短，仅仅九万字，春节期间大概四五个小时看完的，讲述了一个叫许三观的底层小人物在生活困难的年代多次卖血求生的故事，尽管余华老师已经用了很多幽默手法，"),s("strong",[v._v("但是这种幽默感却令人感到不安，如鲠在喉只觉得沉重")]),v._v("。")]),v._v(" "),s("p",[v._v("人生真的太苦了，如果不是用笔的宽容，这是一个彻头彻尾的悲剧。")]),v._v(" "),s("p",[s("img",{attrs:{src:"https://cs-wiki.oss-cn-shanghai.aliyuncs.com/img/20220330162030.png",alt:""}})]),v._v(" "),s("p",[v._v("读余华老师的文字，怎么说，不用忧虑看了几遍都领会不了其中的内涵，因为每句话感觉都挺土的哈哈哈（全书看下来就摘抄了两句 😂），但又很细腻，对，细腻这个词形容很贴切。每个人物都特别真实，给我的感觉就是我站在许三观的身边，随着他走过了一生，陷在这种近乎现实的虚幻旋涡中不能自拔，看到最后特别害怕 Bad Ending，希望许三观别卖血把自己卖死了。")]),v._v(" "),s("p",[v._v('王安忆这样评价说："余华的小说是塑造英雄的，他的英雄不是神，而是世人。但却不是通常的世人，而是违反那么一点人之常情的世人。就是那么一点不循常情，成了英雄……"')]),v._v(" "),s("p",[v._v("全书印象最深的地方，许三观最后一次卖血，也是唯一一个正文中摘抄的句子，大儿子一乐病重求医上海：")]),v._v(" "),s("p",[s("img",{attrs:{src:"https://cs-wiki.oss-cn-shanghai.aliyuncs.com/img/20220205205721.png",alt:""}})]),v._v(" "),s("blockquote",[s("p",[v._v("他要去的地方是上海，路上要经过林浦、北荡、西塘、百里、通元、松林、大桥、安昌门、靖安、黄店、虎头桥、三环洞、七里堡、黄湾、柳村、长宁、新镇。其中林浦、百里、松林、黄店、七里堡、长宁是县城，他要在这六个地方上岸卖血，他要一路卖着血去上海。")])]),v._v(" "),s("p",[v._v("西西弗斯永生都在推回下山的巨石，浮士德与魔鬼订赌遍历凡尘，堂吉诃德持矛孤身冲向风车，许三观一路卖血赶赴上海。从古希腊诸神到底层小人物，大抵上倒是没有什么区别的，"),s("strong",[v._v("在所有史诗中悲情的更为动人，所有的英雄里偏爱最不自量力")]),v._v("。")]),v._v(" "),s("p",[s("img",{attrs:{src:"https://cs-wiki.oss-cn-shanghai.aliyuncs.com/img/20220208121658.png",alt:"希腊神话里的西西弗斯"}})]),v._v(" "),s("br"),v._v(" "),s("p",[v._v("余华说，这是一本关于平等的书。")]),v._v(" "),s("p",[v._v("许三观确实一生都在追求平等，可又矛盾得从未实现过平等。")]),v._v(" "),s("p",[v._v("有些人生来便锦衣玉食，鲜衣怒马，有些人生来就苦难缠身，只能依靠顽强的生命力从乌云密布的人生中搏出一丝曙光。")]),v._v(" "),s("blockquote",[s("p",[v._v("全城人的脸上都是灰颜色，只有李血头的脸上还有红润；全城的脸上的肉都少了，只有李血头的脸上的肉还和过去一样多；全城人都苦着脸，只有李血头笑嘻嘻的")])]),v._v(" "),s("p",[v._v("人民公社、大跃进、饥荒、文革、知青下乡，许三观处在尚处于探索阶段的新中国，其命运的悲剧不仅是个人的悲剧更是时代的悲鸣，苦难文学的意义大抵就在于此。")]),v._v(" "),s("p",[v._v("作为底层的小市民，他无法以更好的方式面对突如其来的变故，只能卖血。")]),v._v(" "),s("p",[v._v("总共十一次卖血，许三观靠着卖血渡过了人生的一个个难关，战胜了命运强加给他的惊涛骇浪。当他老了，知道自己的血再也没有人要时，精神却崩溃了。")]),v._v(" "),s("blockquote",[s("p",[v._v("许三观走出了医院，走到了街上，那时候正是中午，街上全是下班回家的人，一群一群的年轻人飞快地骑着自行车，在街上冲过去，一队背着书包的小学主沿着人行道往前走去。许三观也走在人行道上，他心里充满了委屈，刚才年轻血头的话刺伤了他、他想着年轻血头的话，他老了，他身上的死血比活血多，他的血没人要了，只有油漆匠会要，他想着四十年来，今天是第一次，他的血第一次卖不出去了。四十年来，每次家里遇上灾祸时，他都是靠卖血渡过去的，以后他的血没人要了，家里再有灾祸怎么办？")])]),v._v(" "),s("p",[v._v("我们经常咒骂这狗屁一样的人生却又努力地爱着身边的人，这些人最后成为了我们努力生存下来最重要的意义。")]),v._v(" "),s("p",[v._v("全书为我们展现的是一个男人应该具有的责任与担当，"),s("strong",[v._v("可他的人生际遇又何尝不是一种面对虚妄人生的无奈呢")]),v._v("。")]),v._v(" "),s("br"),v._v(" "),s("p",[v._v("这不能不让我想起前段时间 18 天流调里的北京打工人岳荣贵，他比许三观遭受了更多的苦难（父亲瘫痪在床，母亲摔断了胳膊、自己感染新冠），可结局却没有这般幸运。")]),v._v(" "),s("p",[v._v("他有两个儿子，不幸的是两年前，大儿子走失了，从此音讯全无（警方通报两个半月前已经死亡）。事发后，他到派出所报警，想通过监控或者手机定位来寻找，然而被以各种理由没有进行，事情三个月后才立案，此后这个案子从威海市公安局推回到荣成市公安局，他找过山东省公安厅，最后来到北京。")]),v._v(" "),s("p",[v._v("北京寻子期间，他为了找儿子，辗转过天津，河南，河北，山东十多个城市，每到一处，就靠打零工度日养家，为了省开支，就睡在银行的 ATM 机里。到北京之后，他住在月租 700 的城中村。工作是去各建筑工地扛沙袋、水泥、搬垃圾。一袋水泥 100 斤，扛三四层楼一袋挣 3、4 块钱。工作不固定，哪里有活就去哪里，有时一天就横跨四个区。工作也不分时间，常常凌晨出门，天亮才回。然后三个包子，一碗稀饭，上午睡四五个小时，下午再出去找活。")]),v._v(" "),s("p",[s("img",{attrs:{src:"https://cs-wiki.oss-cn-shanghai.aliyuncs.com/img/20220330162050.png",alt:""}})]),v._v(" "),s("p",[v._v("重述这个故事我依然觉得自己被暗夜笼罩，这份记录详细得让人恐惧。")]),v._v(" "),s("p",[v._v("岳荣贵的流调从元旦深夜 23:30 开始的，举国欢庆的深夜，他在一家酒店工作到凌晨 4：43 分。流调的这 18 天里，他辗转 28 地打工却只有一次就餐记录，也是仅有的一个与工作无关的地点。")]),v._v(" "),s("p",[v._v("麻绳专挑细处断，厄运总缠苦命人。")]),v._v(" "),s("br"),v._v(" "),s("p",[v._v("最后，感谢余华老师冰冷的笔尖下流淌着的温柔，"),s("strong",[v._v("人生半坡，万般险阻，但愿在生活里拼尽全力的人，也都能被生活放过吧")]),v._v("~")])])}),[],!1,null,null,null);_.default=p.exports}}]);